  alist = document.querySelectorAll(".prospect-headshot a");// # <a> for each player
  URLs = [...alist].map(x => x.getAttribute("href"));// # URLs
  //copy(JSON.stringify(URLs));
URLsJSON = JSON.stringify(URLs);
Rstring = URLsJSON.replace("[", "c(").replace("]", "),");
copy(Rstring);
