## a useful function: rev() for strings
strReverse <- function(x)
  sapply(lapply(strsplit(x, NULL), rev), paste, collapse="")
strReverse(c("abc", "Statistics"))
# [1] "cba"        "scitsitatS"

MLB_prospectsdf <- MLB_prospects_by_team %>% 
  lapply(function(.) {tibble(URL=.) %>% mutate(rank=1:n())}) %>% 
  dplyr::bind_rows() %>% 
  mutate(URLreverse=strReverse(URL)) %>% 
  # mutate(MLBIDreverse=stringr::str_split_1(URLreverse, "-")) %>%
  # mutate(MLBIDreverse=tidyr::separate_wider_delim(., URLreverse, "-", names=letters[1], too_many = "drop"))
  mutate(MLBIDreverse=stringr::str_split(URLreverse, "-", simplify=T)[,1]) %>% 
  mutate(MLBID=strReverse(MLBIDreverse)) %>% 
  select(-URLreverse, -MLBIDreverse)
